import React, { useState } from 'react'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faBars} from '@fortawesome/free-solid-svg-icons'
import {useTransition, animated} from 'react-spring'

function Navigation(){
    const [showMenu, setShowMenu] = useState(false)

    const transitions = useTransition(showMenu, null, {
        from: { position: 'absolute', opacity: 0 },
        enter: { opacity: 1 },
        leave: { opacity: 0 },
     })

   //
    //mask 


    let menu 
    let menuMask

    if(showMenu){
        menu = <div >
            The menu
        </div>

        menuMask = 
        <div  onClick={() => setShowMenu(false)}></div>
    }

    return(
        <nav>
            <span className="text-xl">
            <FontAwesomeIcon
                icon={faBars}
                onClick={() => setShowMenu(!showMenu)}
                />
                </span>

            {
                transitions.map(({ item, key, props }) =>
                 item && <animated.div key={key} style={props} className="bg-black-t-50 fixed top-0 left-0 w-full h-full z-50 ">This is the menu</animated.div>
                 )
            }

            {
                transitions.map(({ item, key, props }) =>
                 item && <animated.div key={key} style={props} className="fixed bg-white top-0 left-0 w-4/5 h-full z-50 shadow">This is the menu</animated.div>
                 )
            }

                {menuMask}
                {menu}
        </nav>
    )
}

export default Navigation
