import React, { useState, useEffect } from "react"
import axios from "axios"

function Product(){

    const url = 'https://5efee6c6d18ced0016e40c84.mockapi.io/api/v1/products/3'
    const [product, setProduct] = useState(null)

    let content = null

    useEffect(() => {
        axios.get(url)
        .then(response => {
                setProduct(response.data)
        })
    }, [url])


    if(product){
        return(

        <div>
            <h1>{product.name}</h1>
        </div>
    )
  }

  return(

    <div>
        {content}
    </div>
  )
}

export default Product
