import React, { useState, useEffect } from "react"
import axios from "axios"

function Home(){

    const url = `https://5efee6c6d18ced0016e40c84.mockapi.io/api/v1/products?page=1&limit=10` 
    const [product, setProduct] = useState({loading:false, data: null, error: false})

    useEffect(() => {
        setProduct({
            loading: true,
            data: null,
            error: false
        })

        axios.get(url)
        .then(response => {
                setProduct({
                    loading: false,
                    data: response.data,
                    error: false
                })
        }) 
        .catch(( ) => {
                setProduct({
                    loading: false,
                    data: null,
                    error: true
                })
        })
    }, [url])

    let content = null




    return(
        <div>
            <h1 className="font-bold text-2xl mb-3">Reliable Hire</h1>
            {content}        
        </div>
    )
}

export default Home
