import React, { useState, useEffect } from "react"
import axios from "axios"
import { useParams } from "react-router"

function Product(){

    const { id } = useParams()
    const url = `https://5efee6c6d18ced0016e40c84.mockapi.io/api/v1/products/${id}` 
    const [product, setProduct] = useState({loading:false, data: null})

    let content = null

    useEffect(() => {
        setProduct({
            loading: true,
            data: null
        })

        axios.get(url)
        .then(response => {
                setProduct({loading: false,
                    data: response.data})
        })
    }, [url])

    if(product.loading){
        content = <p>Loading</p>
    }


    if(product.data){
        content =

        <div>
            <h1 className="text-2xl font-bold mb-3">{product.data.name}</h1>

                <div>
                    <img src={product.data.image[0].images} alt={product.name}
                    />
                </div>

                <div className="font-bold text-xl mb3">
                        $ {product.data.price}
                </div>

                <div className="font-bold mb3">
                     {product.data.description}
                </div>
        </div>




  }


  return(

    <div>
        {content}
    </div>
  )
}

export default Product
